import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Create() {
    const navigate = useNavigate();

    // const form = document.getElementById('create');

    // if (form) {form.addEventListener('submit', function(event) {
    //         event.preventDefault();

    //         const formData = new FormData(form);

    //         axios.post('/api/createUser', formData)
    //             .then(response => {
    //                 const data = response.data;

    //                 if (data.success) {
    //                     const success = document.getElementById('success-message');
    //                     success.textContent = 'User created! Now go log in!';
    //                 } else {
    //                     console.error('Failure in creating user', data.error);
    //                 }
    //             })
    //             .catch(error => {
    //                 console.error('Creation request failed', error)
    //             })

    //     })};

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    useEffect(() => {
        setTimeout(function(){
            setMessage('');
        }, 2000);
    }, [message]);

    const handleInputChange = (e, type) => {
        switch(type){
            case 'name':
                setError('');
                setName(e.target.value);
                if (e.target.value === '') {
                    setError('Name has been left blank')
                }
                break;
                case 'email':
                setError('');
                setEmail(e.target.value);
                if (e.target.value === '') {
                    setError('Email has been left blank')
                }
                break;
                case 'password':
                setError('');
                setPassword(e.target.value);
                if (e.target.value === '') {
                    setError('Password has been left blank')
                } else {
                    setMessage(response[0].result);
                    setTimeout(function(){
                        navigate('/');
                    }, 2000)
                }
                break;
                default:
        }
    }

// connects frontend to backend
    function loginSubmit(){
        if(name !== '' && email !== '' && password !== ''){
            var url = 'http://localhost/backend/core/index.php';
            var headers = {
                'Accept': 'application/json',
                'Content-type': 'application/json'
            };
            var Data = {
                name: name,
                email: email,
                password: password
            };
            fetch(url, {
                method: 'POST',
                headers: headers,
                body: JSON.stringify(Data)
            }).then((response) => response.json())
            .then((response) => {
                setMessage(response[0].result);
            }).catch((err) => {
                setError(err);
                console.log(err);
            })
        } else {
            setError('All fields must be filled')
        }
    }
    return(
        <div className='CreateForm'>
            <p>
                {
                    error !== '' ?
                    <span className='error'>{error}</span> :
                    <span className='success'>{error}</span>
                }
            </p>
            <h1>Create your account!</h1>
            <h3>
                <form id='create'>
                    <input 
                        type="text" 
                        id="name" 
                        placeholder="name" 
                        value={name}
                        onChange={(e) => handleInputChange(e, 'name')}
                    /><br/>
                    <input 
                        type="text" 
                        id="email" 
                        placeholder="email" 
                        value={email}
                        onChange={(e)}
                    /><br/>
                    <input 
                        type="password" 
                        id="password" 
                        placeholder="password" 
                        value={password}
                    /><br/>
                    <input 
                        type="password" 
                        id="confirm_password" 
                        placeholder="confirm password" 
                        value={password}
                    /><br/>
                </form>
                <input
                    type='submit'  
                    variant='contained'
                    className='button' 
                    onClick={loginSubmit}>
                    Create
                </input>
            </h3>
        </div>
    );
}

export default Create;


